       function fun_v657fb6e7465ad(){
       var generate_id = "v657fb6e7465ad",
           box = $('#'+generate_id),
           isMouseOver = false;
       if(box.length==0){
           var design_iframe = $('#design_iframe'),
               dom = design_iframe.contents(),
           box = dom.find('#'+generate_id);
       }

       var loop = "",
           speed = "300",
           autoplay = "4000",
           pauseOnMouse = +autoplay && "0" === '1',
           autoHide = "1" === '1';
       if(loop == "1"){
           loop = true;
       }else{
           loop = false;
       }
       var swiper_ids = new Swiper('#'+generate_id+' .swiper-container', {
           autoHeight: true,
           autoplay: parseInt(autoplay),
           speed: parseInt(speed),
           loop: loop,
           autoplayDisableOnInteraction: false,
           slidesPerView : 1,
           // spaceBetween : 20,
           prevButton: '#'+generate_id+' .left_icon',
           nextButton: '#'+generate_id+' .right_icon',
           pagination : '#'+generate_id+' .swiper-pagination',
           paginationClickable :true,
           onInit:function(mySwiper){
               // slide_top_btn();
               box.find('.swiper-container')[0].swipers = function(type){
                   // 当前播放禁止轮播
                   if (autoplay=='0') {return}
                   if (type == 'play') {
                       mySwiper.stopAutoplay();
                   }else{
                       if(!isMouseOver) {
                           mySwiper.startAutoplay();
                       }
                   }
               }
               var top = $(document).scrollTop();
               $(document).scrollTop((top+100));
               $(document).scrollTop((top-100));
               $(document).scrollTop(top);
           },
           onSlideChangeStart:function(swiper){
               box.trigger('togglevideo');
               box.find('.swiper-container .swiper-slide video').each(function(){
                   $(this)[0].pause();
               });

               box.find('.swiper-container .swiper-slide iframe').each(function(){
                   if(/^youtube/.test(this.id) && typeof window.YT !=='undefined') {
                       YT.get(this.id).pauseVideo();
                   }
               });
           },onSlideChangeEnd:function(){
               // slide_top_btn();
           },
       });

       if (pauseOnMouse) {
           box.on('mouseenter', function() {
               swiper_ids.stopAutoplay();
               isMouseOver = true;
           }).on('mouseleave', function() {
               isMouseOver = false;
               var done = false;
               box.find('.swiper-container .swiper-slide iframe').each(function(){
                   if(/^youtube/.test(this.id) && typeof window.YT !=='undefined') {
                       var player = YT.get(this.id);
                       if (player.getPlayerState() === 1) {
                           done=true;
                       }
                   }
               });
               box.find('.swiper-container .swiper-slide video').each(function(){
                   if(!$(this)[0].paused) {
                       done=true;
                   }
               });
               if (!done) {
                   swiper_ids.startAutoplay();
               }
           });
       }

       if (autoHide) {
           box.on('togglevideo', function() {
               var cover = box.find('.top_text');
               if(box.find('.swiper-wrapper').children().eq(swiper_ids.activeIndex).find('video,iframe').is(':visible')) {
                   cover.addClass('top_text--hidden');
               } else {
                   cover.removeClass('top_text--hidden');
               }
           });
       }

       box.on('lazyload', function() {
           setTimeout(() => {
               swiper_ids.updateAutoHeight();
           }, 200);
       });
   }
   </script> 
 <script mos-id="v61aed7e4d2eb1">
   var generate_id = "v61aed7e4d2eb1",
       head_s = $('#'+generate_id);
   function head_nav_w(){
       var nav = head_s.find('.nav'),
           sw = nav[0].scrollWidth,
           w = nav.width(),
           nav_box = head_s.find('nav');
       if(sw > w){
           nav_box.addClass('add');
       }else{
           nav_box.removeClass('add');
       }
       head_s.find('.nav_box')[0].scrollLeft = 0;
       head_s.find('.btn_l').addClass('disabled');
       head_s.find('.btn_r').removeClass('disabled');

       var sloganHeight = head_s.find('.header-slogan-box').outerHeight();
       head_s[0].style.setProperty('--header-offset',-sloganHeight+'px');
   }

   function handleScroll() {
       var thresholds = {
           1: 0,
           nav: 40
       }, key = head_s.attr('switch_fixed');
       if (key in thresholds) {
           var curThreshold = thresholds[key];
           if (typeof curThreshold === "function") {
               curThreshold = curThreshold();
           }
           var top = $(window).scrollTop();
           if (top > curThreshold) {
               head_s.addClass('head_fixed');
           } else {
               head_s.removeClass('head_fixed');
           }
       }
   }

   $(window).on('scroll', handleScroll).trigger('scroll');

   window.addEventListener('resize',head_nav_w);
   head_s.mouseenter(function(){
       if (!this.head_bol) {
           head_nav_w();
           this.head_bol = true;
       }
   })
   head_s.find('#head_button').click(function(){
       $("body,html").toggleClass("head_nav_active");
   })

   head_s.find('.nav_btns').click(function(){
       if ($(this).hasClass('disabled')) {return}
       var nav = head_s.find('.nav'),
           sw = nav[0].scrollWidth,
           w = nav.width(),
           sl_box = head_s.find('.nav_box'),
           sl = sl_box[0].scrollLeft,
           l = $(this).data('left'),
           btn_r = head_s.find('.btn_r'),
           btn_l = head_s.find('.btn_l');
       sl += l;
       if ((sl+w)>sw) {
           btn_r.addClass('disabled');
       }else{
           btn_r.removeClass('disabled');
       }
       if (0>=sl) {
           btn_l.addClass('disabled');
       }else{
           btn_l.removeClass('disabled');
       }
       sl_box.stop().animate({
           scrollLeft: sl
       },200)
   })

   head_s.on('click','.nav li',function(e){
       e.stopPropagation();
       var li = $(this),
           ul = li.find('>ul'),
           tan = e.target;
       if(tan.localName=="a"||$(tan).parents('a').length){
           return
       }else{
           li.toggleClass('draw_act').siblings().removeClass('draw_act').find('ul').hide(200).find('.draw_act').removeClass('draw_act');
           ul.toggle(200);
       }
       return false;
   })

   head_s.on('mouseenter','.nav>li',function(){
       add_nav_li22($(this));
   })
   function par_box_fun(par,fun){
       var id = par.attr('id'),
           li = $('[li_id="'+id+'"]'),
           header_nav_li_box = li.closest('.header_nav_li_box');
       fun(par,li);
       if (header_nav_li_box.length) {
           par_box_fun(header_nav_li_box,fun);
       }
   }
   function add_nav_li22(that,objs){
       if(head_s.find('.nav').css('display')=="block"){return}
       var id = that.attr("li_id"),
           boxs = null;
           ul = that.find('>ul');
       if (ul.length == 0) {
           return;
       }
       var show_fun = function(){
           boxs.stop().fadeIn(200);
           that.addClass("font-color");
           var ids_ = boxs.attr('id');
           if (objs) {
               par_box_fun(objs.par_box,function(id_box,li){
                   id_box.stop().fadeIn(200);
                   li.addClass("font-color");
               })
           }
       }
       var hide_fun = function(n){
           boxs.stop().fadeOut(200);
           that.removeClass("font-color");
           if (objs&&n==2) {
               par_box_fun(objs.par_box,function(id_box,li){
                   id_box.stop().fadeOut(200);
                   li.removeClass("font-color");
               })
           }
       }
       if (id) {
           boxs = $('#'+id);
       }else{
           id = 'li_id_'+new Date().getTime()
